mapper.insert:

insert into basic_schoolclass (pkid, unitid, `name`,
  sortnum, `type`, classcode,
  stageid, gradeid, schoolsystem, 
  schoolyear, createtime, teacherid
  )
values (#{pkid,jdbcType=INTEGER}, #{unitid,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, 
  #{sortnum,jdbcType=INTEGER}, #{type,jdbcType=INTEGER}, #{classcode,jdbcType=VARCHAR}, 
  #{stageid,jdbcType=INTEGER}, #{gradeid,jdbcType=INTEGER}, #{schoolsystem,jdbcType=INTEGER}, 
  #{schoolyear,jdbcType=VARCHAR}, #{createtime,jdbcType=TIMESTAMP}, #{teacherid,jdbcType=INTEGER}
  )

mapper.select:

select pkid, unitid, `name`, sortnum, `type`, classcode, stageid, gradeid, schoolsystem,
schoolyear, createtime, teacherid
from basic_schoolclass
WHERE ${searchStr}
ORDER BY ${orderStr}
