mapper.delete:

delete from basic_space
where typeid = #{pkid,jdbcType=INTEGER};

delete from basic_spacetype
where pkid = #{pkid,jdbcType=INTEGER}

mapper.select:

select pkid, name, sortnum, createtime, remark,unitid
from basic_spacetype
where pkid = #{pkid,jdbcType=INTEGER}


select pkid, name, sortnum, createtime, remark,unitid
from basic_spacetype
WHERE unitid=#{0}
