mapper.insert:

insert into ecb_activity (pkId, author, title, 
  placeId, createtime, starttime, 
  endtime, intro, activitytype, 
  sortnum, photo, content
  )
values (#{pkid,jdbcType=INTEGER}, #{author,jdbcType=VARCHAR}, #{title,jdbcType=VARCHAR}, 
  #{placeid,jdbcType=INTEGER}, #{createtime,jdbcType=TIMESTAMP}, #{starttime,jdbcType=TIMESTAMP}, 
  #{endtime,jdbcType=TIMESTAMP}, #{intro,jdbcType=VARCHAR}, #{activitytype,jdbcType=INTEGER}, 
  #{sortnum,jdbcType=INTEGER}, #{photo,jdbcType=VARCHAR}, #{content,jdbcType=LONGVARCHAR}
  )

mapper.select:

select pkId, author, title, placeId, createtime, starttime, endtime, intro, activitytype,
sortnum, photo, content
from ecb_activity
WHERE ${searchStr}
ORDER BY ${orderStr}
