mapper.delete:

delete from ecb_task
where pkid = #{pkid,jdbcType=INTEGER};

delete from ecb_classtask
where taskid = #{pkid,jdbcType=INTEGER}

mapper.insert:

insert into ecb_task (pkid, subject,type, content,
  sortnum, userid, createtime
  )
values (#{pkid,jdbcType=INTEGER}, #{subject,jdbcType=INTEGER}, #{type,jdbcType=INTEGER}, #{content,jdbcType=VARCHAR},
  #{sortnum,jdbcType=INTEGER}, #{userid,jdbcType=INTEGER}, #{createtime,jdbcType=TIMESTAMP}
  )

mapper.select:

select pkid,subject, type, content, sortnum, userid, createtime
from ecb_task
where pkid = #{pkid,jdbcType=INTEGER}


select pkid,subject, type, content, sortnum, userid, createtime
from ecb_task
WHERE ${searchStr}
ORDER BY ${orderStr}
