mapper.insert:

insert into kq_usercheckinfo (pkid, userid, username, 
  checkphoto, checktime, begintime, 
  endtime, placeid, subject
  )
values (#{pkid,jdbcType=INTEGER}, #{userid,jdbcType=INTEGER}, #{username,jdbcType=VARCHAR}, 
  #{checkphoto,jdbcType=VARCHAR}, #{checktime,jdbcType=TIMESTAMP}, #{begintime,jdbcType=TIMESTAMP}, 
  #{endtime,jdbcType=TIMESTAMP}, #{placeid,jdbcType=INTEGER}, #{subject,jdbcType=VARCHAR}
  )

mapper.select:

select pkid, userid, username, checkphoto, checktime, begintime, endtime, placeid,
subject
from kq_usercheckinfo
WHERE ${searchStr}
ORDER BY ${orderStr}
